      averageWeight: {
          value: 6.0,
          measurementUnit: "kg"
      },
      image: "https://cdn2.bulbagarden.net/upload/b/b2/Spr_5b_025_m.png",
      moreInfo: "https://bulbapedia.bulbagarden.net/wiki/Pikachu_(Pok%C3%A9mon)"
    };

    return (
      <main>
        <h1> Pokedex</h1>
        {/* <section>
          {pokemons.map((element) => (
            <PokemonCard key={element.id} PokeInfo={element}/>
          ))}
        </section> */}
      </main>
    );
  }
}


export default App;
